 private fun isRefreshRequired(trip: TravelItinerary): Boolean {

 try {
 if (trip.lastUpdated == null) {
 return true
 }

 val timeSinceLastCheck = System.currentTimeMillis() - trip.lastUpdated.time
 val timeTillJourney = trip.journeyDate.time - System.currentTimeMillis()

 if (timeSinceLastCheck < Constant.MIN\_ELAPSED\_TIME\_BEFORE\_RESYNC) {
 return false
 }

 if (!trip.isCanceled) {
 if (timeTillJourney >= Constant.INTERVAL\_ONE\_WEEK) {
 if (timeSinceLastCheck >= Constant.INTERVAL\_TWELVE\_HOURS) {
 return true
 }
 } else if (timeTillJourney >= Constant.INTERVAL\_TWO\_DAYS) {
 if (timeSinceLastCheck >= Constant.INTERVAL\_SIX\_HOURS) {
 return true
 }
 } else if (timeTillJourney >= Constant.INTERVAL\_ONE\_HOUR) {
 if (timeSinceLastCheck >= Constant.INTERVAL\_FORTY\_FIVE\_MINUTES) {
 return true
 }
 }

 }

 return false
 } catch (e: Exception) {
 e.printStackTrace()
 return true
 }

 }
}

