using Microsoft.ML.Probabilistic;
using Microsoft.ML.Probabilistic.Distributions;
using Microsoft.ML.Probabilistic.Models;
namespace LatentVariableModel
{
    class Program
    {
        static void Main(string[] args)
        {
            int n = 100; // number of data points
            int k = 2; // number of latent variables
            // generate some data
            double[][] x = new double[n][];
            double[] y = new double[n];
            for (int i = 0; i < n; i++)
            {
                x[i] = new double[k];
                for (int j = 0; j < k; j++)
                {
                    x[i][j] = Gaussian.Sample(0, 1);
                }
                y[i] = x[i][0] + x[i][1] + Gaussian.Sample(0, 0.1);
            }
            // create model
            VariableArray<double>[] theta = new VariableArray<double>[k];
            for (int j = 0; j < k; j++)
            {
                theta[j] = Variable.Array<double>(Variable.Random<double[]>(new Gaussian(0, 1)));
            }
            VariableArray<double> xObs = Variable.Array<double>(Variable.Array<double>(theta[0].Length));
            for (int i = 0; i < n; i++)
            {
                Variable<double> f = Variable.InnerProduct(theta, xObs[i]);
                Variable<double> yObs = Variable.GaussianFromMeanAndVariance(f, 0.1);
                yObs.ObservedValue = y[i];
            }
            // infer latent variables
            InferenceEngine engine = new InferenceEngine();
            for (int j = 0; j < k; j++)
            {
                theta[j].Name = "theta_" + j;
            }
            Console.WriteLine(engine.Infer(theta));
            // make predictions
            double[][] xTest = new double[10][];
            for (int i = 0; i < 10; i++)
            {
                xTest[i] = new double[k];
                for (int j = 0; j < k; j++)
                {
                    xTest[i][j] = Gaussian.Sample(0, 1);
                }
            }
            VariableArray<double> xTestObs = Variable.Array<double>(Variable.Array<double>(theta[0].Length));
            VariableArray<double> yTest = Variable.Array<double>(xTestObs.Length);
            yTest[xTestObs.Range] = Variable.InnerProduct(theta, xTestObs[xTestObs.Range]);
            for (int i = 0; i < 10; i++)
            {
                xTestObs[i].ObservedValue = xTest[i];
            }
            Console.WriteLine(engine.Infer(yTest));
        }
    }
}
